import React, { Component } from 'react';
import './style.css'

class Pokemons extends Component {
    constructor() {
        super();
        this.state = {
            pokemons: []
        };
    }

    componentDidMount() {
        fetch('http://localhost:4242/api/pokemons', {
            headers: {
                "Content-Type" : "application/json"
            }
        })
            .then(res => res.json())
            .then(pokemons => this.setState({pokemons}));
    }

    render() {
        const {pokemons}=this.state;

        return (
                <ul>
                    {pokemons.map(pokemon =>
                       <li key={pokemon.ndex}>
                           <figure>
                                <img src={require(`./image/${pokemon.ndex}.png`)}/>
                           </figure>
                           <div>
                               <p>
                                   {pokemon.ndex}
                               </p>
                               <h5>
                                   {pokemon.nom}
                               </h5>
                           </div>
                       </li>
                    )}
                </ul>
        );
    }
}
export default Pokemons;
