import React, { Component } from 'react';

class Customers extends Component {
    constructor() {
        super();
        this.state = {
            customers: []
        };
    }

    componentDidMount() {
        fetch('http://localhost:4242/api/pokemons')
            .then(res => res.json())
            .then(customers => this.setState({pokemons}, () => console.log('Customers fetched...', customers)));
    }

    render() {
        return (
            <div>
                <h2>Customers</h2>
                <ul>
                    {this.state.pokemons.map(pokemon =>
                       <p key={pokemon.ndex}>
                           #{pokemon.ndex} {pokemon.nom}
                       </p>
                    )}
                </ul>
            </div>
        );
    }
}

export default Customers;
