import React, { Component} from 'react';
import './style.css'

class Pokemons extends Component {
    constructor(props) {
        super(props);
        this.state = {
            loading : false,
            pokemons : res.data.result
        };
    }

    componentDidMount() {
        this.fetchListPokemonId()
    }

    fetchListPokemonId()
    {
        fetch(`http://localhost:4242/pokemons/001`)
            .then(res => res.json())
            .then(pokemons => this.setState({pokemons}))
    }



    render() {
        const {pokemons}=this.state;
        return (
            <div>
                <ul className="container bordure-pokemon">
                    {pokemons.map(pokemon =>
                        <li key={pokemon.ndex}>
                            <a href="PokemonDetails.js">
                                <img src={require(`./image/${pokemon.ndex}.png`)}/>
                            </a>
                            <div className="pokemon_details">
                                <p>
                                    {pokemon.ndex}
                                </p>
                                <h5>
                                    {pokemon.nom}
                                </h5>
                                <span className={pokemon.type1}> {pokemon.type1}</span>
                                <span className={pokemon.type2}> {pokemon.type2}</span>
                            </div>
                        </li>
                    )}
                </ul>
            </div>

        );
    }
}
export default Pokemons;
