import React, { Component } from 'react';

class Pokemons extends Component {
    constructor() {
        super();
        this.state = {
            pokemons: []
        };
    }

    componentDidMount() {
        fetch('http://localhost:4242/api/pokemons')
            .then(res => res.json())
            .then(pokemons => this.setState({pokemons}));
    }

    render() {
        return (
            <div>
                <h2>Pokedex</h2>
                    {this.state.pokemons.map(pokemon =>
                       <ul> key={pokemon.ndex}
                           <li>
                               <a>
                                   <img src={require(`./image/${pokemon.numéro}.png`)} />
                               </a>
                           </li>
                           <li>
                               <a>
                                   <img src={require(`./image/${pokemon.numéro}.png`)} />
                               </a>
                           </li>
                       </ul>
                    )}
            </div>
        );
    }
}

export default Pokemons;
