import React, { Component } from 'react';
import './App.css';
import Pokemon from './liste/Pokemons';
import Details from './liste/PokemonDetails'
import './liste/style.css'

class App extends Component {

    render() {
        let url=window.location.pathname;
        let folder;
        function page(url) {
            if(url === "localhost:3000")
            {
                folder = "</Pokemon>"
            }
            else {
                folder="</Details>"
            }
        }
    return (
        <div className="App">
              <h2> Pokedex</h2>
            <br></br>
            {folder}
        </div>
    );
  }
}

export default App;
