import React from 'react';
import axios from 'axios'


export default class Pokemons extends React.Component{

    state ={ pokemons: []};

  componentDidMount() {
      this.fetchListPokemon()
  }

  fetchListPokemon()
  {
      axios
          .get("http://localhost:4242/api/pokemons")
          .then(res => this.setState({pokemons: res.pokemons}))
          .catch(error => this.setState({error}))
  }

  render() {
    return (
        <div>
          <h2>Pokemon lists:</h2>
          <ul>
              {this.state.pokemons.map(el =>(
                <li>
                    {el.name}: {el.ndex}
                </li>
                  ))}
          </ul>
        </div>
    );
  }
}
