import React, { Component} from 'react';
import './style.css'

class Pokemons extends Component {
    constructor(props) {
        super(props);
        this.state = {
            pokemons: {},
        };
    }

    componentDidMount() {
        this.fetchListPokemonId()
    }

    fetchListPokemonId()
    {
        fetch(`http://localhost:4242/pokemons/001`)
            .then(res => res.json())
            .then(pokemons => this.setState({"pokemons": pokemons}));
    }



    render() {
        const {pokemons}=this.state;
        return (
            <div>
                <ul className="container bordure-pokemon">
                    {pokemons}
                        <li>
                            <a href="PokemonDetails.js">
                                <img src={require(`./image/001.png`)}/>
                            </a>
                            <div className="pokemon_details">
                                <p>

                                </p>
                                <h5>

                                </h5>

                            </div>
                        </li>
                    )}
                </ul>
            </div>

        );
    }
}
export default Pokemons;
