import React, { Component } from 'react';
import './style.css'

class Pokemons extends Component {
    constructor() {
        super();
        this.state = {
            pokemons: []
        };
    }

    componentDidMount() {
        fetch('http://localhost:4242/api/pokemons')
            .then(res => res.json())
            .then(pokemons => this.setState({pokemons}));
    }

    render() {
        const {pokemons}=this.state;

        return (
                <ul className="container">
                    {pokemons.map(pokemon =>
                       <li key={pokemon.ndex}>
                                <img src={require(`./image/${pokemon.ndex}.png`)}/>
                           <div className="pokemon_details">
                               <p>
                                   {pokemon.ndex}
                               </p>
                               <h5>
                                   {pokemon.nom}
                               </h5>
                           </div>
                       </li>
                    )}
                </ul>
        );
    }
}
export default Pokemons;
