import React, {Component} from 'react';
import './style2.css'

class Pokemons extends Component {
    constructor(props) {
        super(props);
        this.state = {
            pokemon: [],

        };
    }

    componentDidMount() {
        this.fetchListPokemonId()
    }

    fetchListPokemonId() {
        let id;
        if (localStorage.getItem("id") === "" || localStorage.getItem("id") === null) {
            id = "";
        } else {
            id = localStorage.getItem("id")
        }

        fetch(`http://localhost:4242/pokemons/${id}`)
            .then(res => res.json())
            .then(pokemon => this.setState({pokemon}))
    }

    back() {
        localStorage.removeItem("id")
        document.location.href = "http://localhost:3000"
    }


    render() {
        const {pokemon} = this.state;

        return (
            <div className="container bordure-pokemon">
                <div className="row">
                    <div className="col">
                        <input id="back" className="btn btn-primary" type="submit"  onClick={this.back} value={`<  ${pokemon.ndex}   ${pokemon.nom}`}></input>
                        <br></br><br></br>
                        <figure>
                            <img className="details-pokemon" src={`https://assets.pokemon.com/assets/cms2/img/pokedex/detail/${pokemon.ndex}.png`}/>
                        </figure>
                   </div>
                    <div className="col">
                        <table className="table">
                            <thead>
                            <tr class="bg-primary">
                                <th scope="col">Espece</th>
                                <th scope="col">Taille</th>
                                <th scope="col">Poids</th>
                            </tr>
                            </thead>
                            <tbody>
                            <tr class="bg-primary">
                                <td>{pokemon.espece}</td>
                                <td>{pokemon.taille}</td>
                                <td>{pokemon.poids}</td>
                            </tr>
                            </tbody>
                        </table>
                    </div>
                </div>
            </div>

        );
    }
}

export default Pokemons;
