import React, { Component} from 'react';
import './style2.css'
import './Pokemons'

class Pokemons extends Component {
    constructor(props) {
        super(props);
        this.state = {
            pokemon: [],

        };
    }

    componentDidMount() {
        this.fetchListPokemonId()
    }

    fetchListPokemonId() {
        let id;
        if (localStorage.getItem("id") === "" || localStorage.getItem("id") === null) {
            id = "";
        } else {
            id = localStorage.getItem("id")
        }

        fetch(`http://localhost:4242/pokemons/${id}`)
            .then(res => res.json())
            .then(pokemon => this.setState({pokemon}))
    }

    back()
    {
        localStorage.removeItem("id")
        document.location.href="http://localhost:3000"
    }


    render() {
        const {pokemon}=this.state;

        return (
            <div>

                <div className="container bordure-pokemon">
                    <div>
                        <a id="back" className="btn btn-primary" onClick={this.back}> cliquer ici</a>
                            <img src={`https://assets.pokemon.com/assets/cms2/img/pokedex/detail/${pokemon.ndex}.png`}/>
                            </div>
                </div>
            </div>

        );
    }
}
export default Pokemons;
