import React, { Component } from 'react';

class Pokemon extends Component {
  constructor() {
    super();
    this.state = {
      pokemon: []
    };
  }

  componentDidMount() {
    fetch('/api/pokemon')
      .then(res => res.json())
      .then(pokemon => this.setState({pokemon}, () => console.log('Pokemon fetched...', pokemon)));
  }

  render() {
    return (
      <div>
        <h2>Customers</h2>
        <ul>
        {this.state.pokemon.map(pokemon =>
          <li key={pokemon.nom}>{pokemon.ndex} {pokemon.jdex}</li>
        )}
        </ul>
      </div>
    );
  }
}

export default Pokemon;
