import React, { Component} from 'react';
import './style.css'

class Pokemons extends Component {
    constructor(props) {
        super(props);
        this.state = {
            pokemons: [],
        };
    }

    componentDidMount() {
        this.fetchListPokemonId()
    }

    fetchListPokemonId()
    {
        fetch(`http://localhost:4242/pokemons/001`)
            .then(res => res.json())
            .then(pokemons => this.setState({pokemons}));
    }



    render() {
        const {pokemons}=this.state;

        return (
            <div>
                <ul className="container bordure-pokemon">
                    <li>
                            <a href="PokemonDetails.js">
                                <img src={require(`./image/${this.state.pokemons.ndex}.png`)}/>
                            </a>
                            <div className="pokemon_details">
                                <p>
                                    {this.state.pokemons.ndex}
                                </p>
                                <h5>
                                    {this.state.pokemons.nom}
                                </h5>
                                <span className={this.state.pokemons.type1}> {this.state.pokemons.type1}</span>
                                <span className={this.state.pokemons}> {this.state.pokemons.type2}</span>
                            </div>
                        </li>
                </ul>
            </div>

        );
    }
}
export default Pokemons;
