import React, { Component } from 'react';
import './App.css';
import Pokemon from './liste/Pokemons';
import './liste/style.css'

class App extends Component {

    render() {
        let button;
        const url=window.location.pathname;
        const url_path=url.split("/");
        const path=url_path[1];

        if(url !== "localhost:3000" && localStorage.getItem("id") === "")
        {
            localStorage.removeItem("id")
            localStorage.setItem("id",path);
            document.location.href="http://localhost:3000";
        }

           if(localStorage.getItem("id") === "")
            {
                button=<Pokemon/>
            }
            else if(localStorage.getItem("id") !== "")
           {
               const Details = require("liste/PokemonDetails");
               button=<Details/>
            }
            return (
                <div className="App">
                    <h2> Pokedex</h2>
                    <br></br>
                    {button}
                </div>
            );

        }
}

export default App;
