import React from 'react';


export default class Pokemons extends React.Component{

    state ={ pokemons: []};

  componentDidMount() {
      this.fetchListPokemon()
  }

  async fetchListPokemon()
  {
      const response = await fetch("https://localhost:4242/api/pokemons");
      const data = await response.json()
      this.setState({pokemons: data.pokemons})

  }

  render() {
      const {pokemons} = this.state;
    return (
        <div>
          <h2>Pokemon lists:</h2>
          <ul>
              {pokemons.map(pokemons =>(
                  <p key={pokemons.ndex}>
                      #{pokemons.ndex} {pokemons.nom}
                  </p>
                  ))}
          </ul>
        </div>
    );
  }
}
