import React, { Component } from 'react';
import './style.css'

class Pokemons extends Component {
    constructor() {
        super();
        this.state = {
            pokemons: []
        };
    }

    componentDidMount() {
        fetch('http://localhost:4242/api/pokemons')
            .then(res => res.json())
            .then(pokemons => this.setState({pokemons}));
    }

    render() {
        const {pokemons}=this.state;
        const {ndex}=pokemons.map(pokemon=>pokemon.ndex);


        return (
            <div>
                <h2>Pokedex</h2>
                <ul>
                    <li> {ndex}</li>
                    {pokemons.map(pokemon =>
                       <li key={ndex}>
                          <a>
                              <img src={require(`./image/${pokemon.ndex}.png`)}/>
                          </a>
                       </li>
                    )}
                </ul>
            </div>
        );
    }
}

export default Pokemons;
