import React, { Component} from 'react';
import './style.css'
import './Pokemons'

class Pokemons extends Component {
    constructor(props) {
        super(props);
        this.state = {
            pokemon: [],
            term: ''
        };
    }

    componentDidMount() {
        this.fetchListPokemonId()
    }

    fetchListPokemonId()
    {
        fetch(`http://localhost:4242/pokemons/001`)
            .then(res => res.json())
            .then(pokemon => this.setState({pokemon}))
    }

    searchHandler(event)
    {
        this.setState({term: event.target.value})
    }


    render() {
        const {pokemon, term}=this.state;


        return (
            <div>
                <ul className="container bordure-pokemon">
                        <li key={pokemon.ndex}>
                           <a href="">
                                <img src={`https://assets.pokemon.com/assets/cms2/img/pokedex/detail/${pokemon.ndex}.png`}/>
                               <input type="text" className="form-control" placeholder="Search for name or number"
                                      onChange={this.searchHandler}
                                      value={term}/>
                            </a>
                            <div className="pokemon_details">
                                <p>
                                    {pokemon.ndex}
                                </p>
                                <h5>
                                    {pokemon.nom}
                                </h5>
                                <span className={pokemon.type1}> {pokemon.type1}</span>
                                <span className={pokemon.type2}> {pokemon.type2}</span>
                            </div>
                        </li>
                </ul>
            </div>

        );
    }
}
export default Pokemons;
