import React, { Component } from 'react';
import './style.css'

class Pokemons extends Component {
    constructor() {
        super();
        this.state = {
            pokemons: []
        };
    }

    componentDidMount() {
        fetch('http://localhost:4242/api/pokemons')
            .then(res => res.json())
            .then(pokemons => this.setState({pokemons}));
    }

    render() {
        const {pokemons}=this.state

        function tri(pokemons)
        {
            if (pokemons.nom < pokemons.nom) return -1;
            else if (pokemons.nom == pokemons.nom) return 0;
            else return 1;
        }
        return (
            <div>
                <h2>Pokedex</h2>
                <ul>
                    {pokemons.map(pokemon =>
                       <li key={pokemon.ndex}>
                          <a>
                              <img src={require(`./image/${pokemon.ndex}.png`)}/>
                          </a>
                       </li>
                    )}
                </ul>
            </div>
        );
    }
}

export default Pokemons;
