import React, { Component } from 'react';
import './style.css'

class Pokemons extends Component {
    constructor() {
        super();
        this.state = {
            pokemons: []
        };
    }

    componentDidMount() {
        fetch('http://localhost:4242/api/pokemons')
            .then(res => res.json())
            .then(pokemons => this.setState({pokemons}));
    }

    render() {
        const {pokemons}=this.state;

        function tri(a,b) {
            return (a.ndex > b.ndex) ? 1 : -1;
        }
        const pokemon=tri(pokemons)

        return (
            <div>
                <h2>Pokedex</h2>
                <ul>
                    {pokemon.map(pokemonz =>
                       <li key={pokemonz.ndex}>
                          <a>
                              <img src={require(`./image/${pokemonz.ndex}.png`)}/>
                          </a>
                       </li>
                    )}
                </ul>
            </div>
        );
    }
}

export default Pokemons;
