import React from 'react';
import axios from 'axios'


export default class Pokemons extends React.Component{

    state ={ pokemons: []};

  componentDidMount() {
      this.fetchListPokemon()
  }

  fetchListPokemon()
  {
      axios
          .get("http://localhost:4242/api/pokemons");



  }

  render() {
      const {pokemons} = this.state;
    return (
        <div>
          <h2>Pokemon lists:</h2>
          <ul>
              {pokemons.map(pokemons =>(
                  <p key={pokemons.ndex}>
                      #{pokemons.ndex} {pokemons.nom}
                  </p>
                  ))}
          </ul>
        </div>
    );
  }
}
