import React, { Component } from 'react';
import './customers.css';

class Pokemons extends Component {
    constructor() {
        super();
        this.state = {
            pokemons: []
        };
    }

    componentDidMount() {
        fetch('/api/pokemons')
            .then(res => res.json())
            .then(pokemons => this.setState({pokemons}, () => console.log('Customers fetched...', pokemons)));
    }

    render() {
        return (
            <div>
                <h2>Customers</h2>
                <ul>
                    {this.state.pokemons.map(customer =>
                        <li key={pokemons.id}>{pokemons.firstName} {pokemons.lastName}</li>
                    )}
                </ul>
            </div>
        );
    }
}

export default Pokemons;
